<template>
  <v-container fluid py-0 px-0>
    <v-card color="gray lighten-3" @click.native="onChat">
      <v-layout row wrap>
        <v-flex xs3 pl-2 pt-2>
          <v-avatar size="90">
            <img :src="picture" contain></img>
          </v-avatar>
        </v-flex>
        <v-flex xs6>
          <v-card-title>
            <span class="title">{{ link.username }}</span><br>
            <span class="body-1">Your name: {{ currentUsername }}</span>
          </v-card-title>
        </v-flex>
        <v-flex xs3>
          <v-card-actions>
            <v-container fluid grid-list-md>
              <v-layout column wrap>
                <v-flex xs2 class="text-xs-center">
                  <v-btn color="warning" icon @click.stop="onChangeName"><v-icon>edit</v-icon></v-btn>
                </v-flex>
                <v-flex xs2 class="text-xs-center">
                  <v-btn color="error" icon @click.stop="onRemove"><v-icon>block</v-icon></v-btn>
                </v-flex>
                <v-flex xs12 py-0>
                  <v-dialog v-model="dialog">
                    <v-card>
                      <v-card-title>
                        <v-text-field v-model="newUsername" label="Select a new display name" required></v-text-field>
                      </v-card-title>
                      <v-card-actions>
                        <v-btn color="primary" @click="onDialogSave">Save</v-btn>
                        <v-btn color="secondary" @click="onDialogCancel">Cancel</v-btn>
                      </v-card-actions>
                    </v-card>
                  </v-dialog>
                </v-flex>
              </v-layout>
            </v-container>
          </v-card-actions>
        </v-flex>
      </v-layout>
    </v-card>
  </v-container>
</template>

<script>
  import * as firebase from 'firebase'
  export default {
    data () {
      return {
        currentUsername: '',
        newUsername: '',
        dialog: false,
        image: ''
      }
    },
    computed: {
      picture () {
        if (this.image.length > 1) {
          return this.image
        }
        return 'data:image/png;base64,'
      }
    },
    props: ['link'],
    methods: {
      onChat () {
        this.$store.commit('setCurrentChat', this.link.chat)
        this.$router.push('/chat')
      },
      onRemove () {
        this.$store.dispatch('removeLink', this.link)
      },
      onChangeName () {
        this.dialog = true
      },
      onDialogCancel () {
        this.newUsername = ''
        this.dialog = false
      },
      onDialogSave () {
        var newLink = {
          chat: this.link.chat,
          user: this.$store.getters.user.id,
          username: this.newUsername
        }
        firebase.database().ref('links/' + this.link.user + '/' + this.$store.getters.user.id).set(newLink)
        this.currentUsername = this.newUsername
        this.newUsername = ''
        this.dialog = false
      }
    },
    mounted () {
      firebase.database().ref('links/' + this.link.user + '/' + this.$store.getters.user.id).once('value', (snapshot) => {
        console.log(snapshot.val())
        this.currentUsername = snapshot.val().username
      })
      firebase.database().ref('users/' + this.link.user).once('value', (snapshot) => {
        this.image = snapshot.val().imageUrl
      })
    }
  }
</script>